mapper.select:
SELECT * FROM (SELECT row_number() OVER (ORDER BY signup_date DESC) AS idx, tbl_member.* FROM tbl_member) 
    WHERE idx BETWEEN #{startRange} AND #{endRange}

    	AND ${searchOption} LIKE '%'||#{searchKeyword}||'%'

mapper.update:
DELETE FROM tbl_member 
	WHERE user_id IN

	#{user_id}
